import dynamic from "next/dynamic"
import { useState } from 'react'

const Rust = dynamic({
  loader: async () => {
    const rust = await import('../pkg')

    return (
      (props) => {
        const [board, setBoard] = useState(rust.initial_state())

        return (
          <>
            <form>
              Board:
              <input
                type="text"
                value={board}
                onChange={setBoard(board.split(' '))}
              />
            </form>
            <div>{rust.solve(board)}</div>
          </>
        )
      })
  },
})

export default Rust
