import dynamic from "next/dynamic"
import { useState } from 'react'

const Rust = dynamic({
  loader: async () => {
    const rust = await import('../pkg')

    return (
      (props) => {
        const [board, setBoard] = useState(rust.initial_state())

        return (
          <>
            <input onChange={event => setBoard(event.target.value.split(' '))} />
            <div>{rust.solve(board)}</div>
          </>
        )
      })
  },
})

export default Rust
