import dynamic from "next/dynamic"
import { useState, createRef } from 'react'

const Rust = dynamic({
  loader: async () => {
    const rust = await import('../pkg')

    return (
      (props) => {
        const [board, setBoard] = useState(rust.initial_state())

        const input = createRef()

        const handleSubmit = (event) => {
          const move = rust.solve(input.current.value.split(' '))
          alert("mejor movimieto: " + move)
          event.preventDefault()
        }

        return (
          <>
            <form onSubmit={() => handleSubmit}>
              Board:
              <label>
                <input
                  type="text"
                  ref={input}
                />
              </label>
              <input type="submit" value="Submit" />
            </form>
          </>
        )
      })
  },
})

export default Rust
