import dynamic from "next/dynamic"
import { useState, createRef } from 'react'

const Rust = dynamic({
  loader: async () => {
    const rust = await import('../pkg')

    return (
      (props) => {
        const [board, setBoard] = useState(rust.initial_state())

        const input = createRef()
        const solve = rust.solve(board)

        const handleSubmit = (event) => {
          event.preventDefault()
          alert("mejor movimieto: " + rust.solve(input))         
        }

        return (
          <>
            <form onSubmit={handleSubmit}>
              Board:
              <input
                type="text"
                ref={input}
              />
            </form>
            <div>{solve}</div>
          </>
        )
      })
  },
})

export default Rust
